import React, { useState, useEffect, ReactDOM, useContext } from 'react';
import './App.css';
import { Continents } from './components/Continents';
import { Regions } from './components/Regions';
import { Countries } from './components/Countries';
import { Accordion, Card, Button } from 'react-bootstrap';
//import { } from 'react-bootstrap/AccordionToggle';
import 'bootstrap/dist/css/bootstrap.css';

function App() {
    const [continents, setContinents, ] = useState([]);
    const [continent, setContinent] = useState([]);
    const [regions, setRegions] = useState([]);
    const [region, setRegion] = useState([]);
    const [countries, setCountries] = useState([]);
    const [country, setCountry] = useState([]);
    const [cities, setCities] = useState([]);
    const [city, setCity] = useState([]);

    const GET_COUNTRIES = `
query getCountries {
countries(filters: {continent: ${continent}, region: ${region}}) {
edges {
node {
code 
name 
continent 
region 
surfacearea 
indepyear 
population 
lifeexpectancy 
gnp 
gnpold 
localname 
governmentform 
headofstate 
capital 
code2
}
}
}
}
`;

    const GET_CITIES = `
query getCities {
cities(filters: {countrycode: "USA"}) {
edges {
node {
id 
name 
countrycode 
district 
population 
}
}
}
}

`;
    useEffect(() => {
        fetch('/continents').then(res => res.json()).then(data => {
            setContinents(data.continents);
        });

        //fetch('/graphql?query=' + GET_COUNTRIES).then(res => res.json())
        //    .then(data => {
        //        for (const cont of continents) {
        //            fetch('/regions/' + continent).then(res => res.json()).then(data => {
        //                cont_reg.cont = data.regions;
        //                console.log(cont);
        //            });
        //        }
        //        setCountries(data.countries);
        //    });

        
        //fetch('/graphql?query=' + GET_CITIES).then(res => res.json())
        //    .then(data => {
        //        setCities(data.cities);
        //    });
    }, []);

    function onContinentClick(cont) {
        setContinent(cont);

        fetch('/regions/' + cont).then(res => res.json())
            .then(data => {
                setRegions(data.regions);
            })
        const currentEventKey = useContext(AccordionContext);
        console.log(currentEventKey);
    }


    return (
        <div className="App App-header">
            <h1 className="App-title">World Database</h1>
            <Accordion>
                <Card>
                    <Accordion.Toggle className="App-accordion-toggle" as={Card.Header} eventKey="0">
                        Continent
                    </Accordion.Toggle>
                    <Accordion.Collapse eventKey="0" id="0">
                        <Card.Body>
                            <Continents continents={continents} onClick={onContinentClick} />
                        </Card.Body>
                    </Accordion.Collapse>
                </Card>
                <Card>
                    <Accordion.Toggle className="App-accordion-toggle" as={Card.Header} eventKey="1">
                        Regions
                    </Accordion.Toggle>
                    <Accordion.Collapse eventKey="1" id="1">
                        <Card.Body>
                            <Regions regions={regions} continent={continent} />
                        </Card.Body>
                    </Accordion.Collapse>
                </Card>
            </Accordion>
        </div>
    );
}

export default App;
