import React, { useState, useEffect } from 'react';
import './App.css';
import { Continents } from './components/Continents';
import { Regions } from './components/Regions';
import { Countries } from './components/Countries';

function App() {
    const [continents, setContinents] = useState([]);
    const [regions, setRegions] = useState([]);
    const [countries, setCountries] = useState([]);
    const [cities, setCities] = useState([]);
    const GET_COUNTRIES = `
{query getCountries {\n
countries(filters: {continent: "North America", region: "North America"} {\n
edges {\n
node {\n
code\n
name\n
continent\n
region\n
surfacearea\n
indepyear\n
population\n
lifeexpectancy\n
gnp\n
gnpold\n
localname\n
governmentform\n
headofstate\n
capital\n
code2\n
}\n
}\n
}\n
}\n
`;

    useEffect(() => {
        fetch('/continents').then(res => res.json()).then(data => {
            setContinents(data.continents);
        });

        fetch('/regions/Asia').then(res => res.json()).then(data => {
            setRegions(data.regions);
        });

        fetch('/graphql', {
            method: 'GET',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({query: GET_COUNTRIES}),
        }).then(res => res.json())
            .then(data => {
            console.log(data);
        });

        //fetch()
    }, []);

    return (
        <div className="App">
            <Continents continents={continents} />
            <Regions regions={regions} />
            <Countries countries={countries} />
        </div>
    );
}

export default App;
