import React from 'react';
import { ButtonGroup, Button } from 'react-bootstrap';


export const Regions = ({ regions, continent }) => {
    let content = "Please select continent to see the regions.";

    if (regions.length != 0) {
        content = (
            <div>
                <h2 className="App-accordion-toggle">{continent}</h2>
                <ButtonGroup size="lg">
                    {regions.map(reg =>
                        <Button variant="secondary" key={reg}>
                            {reg}
                        </Button>
                    )}
                </ButtonGroup>
            </div>
        );
    }
    return (
        <div>{content}</div>
    )
}
