import React, { useState, useEffect } from 'react';
import { Button } from 'react-bootstrap';
import BootstrapTable from 'react-bootstrap-table-next';
import { FaPencilAlt, FaTrashAlt } from "react-icons/fa";
import filterFactory, { textFilter } from 'react-bootstrap-table2-filter';
import paginationFactory from 'react-bootstrap-table2-paginator';
import { AddCityForm } from './AddCityForm';

export const Cities = ({ country }) => {
    const [cities, setCities] = useState([]);
    const [show, setShow] = useState(false);
    const [reloadSwitch, setReloadSwitch] = useState(false);
    
    useEffect(
        () => {
            const GET_CITIES = `
query getCities {
cities(filters: {countrycode: "${country.code}"}) {
edges {
node {
id 
name 
countrycode 
district 
population 
}
}
}
}

`;

            fetch('/graphql?query=' + GET_CITIES).then(res => res.json())
                .then(data => {
                    setCities(data.data.cities.edges);
                });
        }, [country, reloadSwitch]);

    if (!country || country.length === 0) {
        if (cities.length !== 0) {
            setCities([]);
        }

        return <div></div>
    }

    let city_ls = [];
    for (const city of cities) {
        city_ls.push(city.node);
    }

    const columns = [{
        dataField: 'id',
        text: 'ID'
    }, {
        dataField: 'name',
        text: 'City Name',
            sort: true,
            filter: textFilter()
    }, {
        dataField: 'district',
        text: 'District',
            sort: true,
            filter: textFilter()
    }, {
        dataField: 'population',
        text: 'Population',
        sort: true
    }];

    const expandRow = {
        renderer: row => (
            <div className="App-data-buttons">
                <Button variant="success" id={row.id}><FaPencilAlt /> Edit City</Button>
                <Button variant="secondary" id={row.id} onClick={() => onDeleteBtnClick(row.id)}><FaTrashAlt /> Delete City</Button>
            </div>
        ),
        showExpandColumn: true
    };

    function onAddBtnClick() {
        setShow(!show);
    }

    function reload() {
        setReloadSwitch(!reloadSwitch);
    }

    function onDeleteBtnClick(id) {
        const DELETE_CITY = `
mutation deleteCity {
deleteCity(id: ${id}) {
status 
}
}`;

        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ query: DELETE_CITY })
        };

        fetch('/graphql', requestOptions)
            .then(response => response.json())
            .then(data => {
                console.log(data);
            });
    }

    return (
        <div className="App-data-wrap">
            <p>Cities in Country</p>
            <div>
                <Button variant="success" className="App-button" onClick={() => onAddBtnClick()}>Add City</Button>
                {<AddCityForm show={show} country={country} onClick={reload} />}
                </div>
            <div className="App-table">
                <BootstrapTable wrapperClasses="table-responsive" keyField="id" data={city_ls} columns={columns} filter={filterFactory()} expandRow={expandRow} pagination={paginationFactory()} striped hover condensed />
            </div>
        </div>
    )
}
