import React from 'react';
import { Dropdown } from 'react-bootstrap';


export const Continents = ({ continents, onClick }) => {
    return (
        <Dropdown>
            <Dropdown.Toggle variant="success" id="continent-dropdown">
                Continent
            </Dropdown.Toggle>
            <Dropdown.Menu>
                {continents.map(cont =>
                    <Dropdown.Item onClick={() => onClick(cont)}>
                        {cont}
                    </Dropdown.Item>
                )}
            </Dropdown.Menu>
        </Dropdown>
    )
}
