import React, { useState } from 'react';
import { Button } from 'react-bootstrap';
import BootstrapTable from 'react-bootstrap-table-next';
import { FaPencilAlt, FaTrashAlt } from "react-icons/fa";
import filterFactory, { textFilter } from 'react-bootstrap-table2-filter';
import paginationFactory from 'react-bootstrap-table2-paginator';

export const Cities = ({ country }) => {
    const [cities, setCities] = useState([]);
    const GET_CITIES = `
query getCities {
cities(filters: {countrycode: "${country.code}"}) {
edges {
node {
id 
name 
countrycode 
district 
population 
}
}
}
}

`;

    if (!country) {
        return <div></div>
    }

    fetch('/graphql?query=' + GET_CITIES).then(res => res.json())
        .then(data => {
            setCities(data.data.cities.edges);
        });

    let city_ls = [];
    for (const city of cities) {
        city_ls.push(city.node);
    }

    const columns = [{
        dataField: 'id',
        text: 'ID'
    }, {
        dataField: 'name',
        text: 'City Name',
            sort: true,
            filter: textFilter()
    }, {
        dataField: 'district',
        text: 'District',
            sort: true,
            filter: textFilter()
    }, {
        dataField: 'population',
        text: 'Population',
        sort: true
    }];

    const defaultSorted = [{
        dataField: 'population',
        order: 'desc'
    }];

    const expandRow = {
        renderer: row => (
            <div className="App-data-buttons">
                <Button variant="success" id={row.id}><FaPencilAlt /> Edit City</Button>{' '}
                <Button variant="secondary" id={row.id}><FaTrashAlt /> Delete City</Button>
            </div>
        ),
        showExpandColumn: true
    };

    return (
        <div className="App-data-wrap">
            <p>Cities in Country</p>
            <Button variant="success" className="App-button">Add City</Button>
            <div className="App-table">
                <BootstrapTable wrapperClasses="table-responsive" keyField="id" data={city_ls} columns={columns} defaultSorted={defaultSorted} filter={filterFactory()} expandRow={expandRow} pagination={paginationFactory()} striped hover condensed />
            </div>
        </div>
    )
}
