import React, { useState, useEffect } from 'react';
import { From, Button, Form, Col } from 'react-bootstrap';

export const AddCityForm = ({ show, country }) => {
    const [validated, setValidated] = useState(false);

    const handleSubmit = (event) => {
        const form = event.currentTarget;
        console.log(form);
        if (form.checkValidity() === false) {
            event.preventDefault();
            event.stopPropagation();
        }

        setValidated(true);

        const GET_CITIES = `
mutation addCity {
createCity(name: "") {
edges {
node {
id 
name 
countrycode 
district 
population 
}
}
}
}

`;

        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify()
        }
    };

    return (
        <Form inline noValidate validated={validated} onSubmit={handleSubmit}>
            <Form.Row className="align-items-center">
                <Col xs="auto">
                    <Form.Control className="mb-4" type="text" placeholder="City Name" required />
                </Col>
                <Col xs="auto">
                    <Form.Control className="mb-4" type="text" placeholder="District" required />
                </Col>
                <Col xs="auto">
                    <Form.Control className="mb-4" type="number" placeholder="Population" required />
                </Col>
                <Col xs="auto">
                    <Button className="mb-4" variant="success" type="submit">
                        Submit
                    </Button>
                </Col>
            </Form.Row>
        </Form>
    )
}
