import React from 'react';
import { ButtonGroup, Button } from 'react-bootstrap';
import BootstrapTable from 'react-bootstrap-table-next';
import { FaPencilAlt, FaTrashAlt } from "react-icons/fa";


export const Cities = ({ cities }) => {
    if (!cities || cities.length == 0) {
        return <div></div>
    }

    let city_ls = [];
    for (const city of cities) {
        city_ls.push(city.node);
    }

    const columns = [{
        dataField: 'id',
        text: 'ID'
    }, {
        dataField: 'name',
            text: 'City Name',
        sort: true
    }, {
        dataField: 'district',
            text: 'District',
        sort: true
        }, {
            dataField: 'population',
            text: 'Population',
            sort: true
        }];

    const defaultSorted = [{
        dataField: 'percentage',
        order: 'desc'
    }];

    const expandRow = {
        renderer: row => (
            <ButtonGroup>
                <Button variant="secondary" id={row.id}>Edit City</Button>
                <Button variant="secondary" id={row.id}>Delete City</Button>
            </ButtonGroup>
        ),
        showExpandColumn: true
    };

    return (
        <div className="App-language">
            <p>Cities in Country</p>
            <div className="App-table">
            <BootstrapTable wrapperClasses="table-responsive" keyField="language" data={city_ls} columns={columns} defaultSorted={defaultSorted} expandRow={expandRow} striped hover condensed>
            <thead>
                <tr>
                    <th></th>
                    <th></th>
                    <th>Delete</th>
                </tr>
            </thead>
            <tbody>
                {cities.map(city =>
                    <tr>
                        <th className="App-table-body">{city.node.id}</th>
                        <th className="App-table-body">{city.node.name}</th>
                        <th className="App-table-body">{city.node.district}</th>
                        <th className="App-table-body">{city.node.population}</th>
                        <th className="App-table-body"><Button><FaPencilAlt /></Button></th>
                        <th className="App-table-body"><Button><FaTrashAlt /></Button></th>
                    </tr>
                )}
            </tbody>
                </BootstrapTable>
                </div>
            </div>
    )
}
