import React, { useState, useEffect } from 'react';
import './App.css';
import { Continents } from './components/Continents';
import 'bootstrap/dist/css/bootstrap.css';
import { Regions } from './components/Regions';
import { Countries } from './components/Countries';
import { CountryAttr } from './components/CountryAttr';
//import { Language } from './components/Language';

function App() {
    const [continents, setContinents] = useState([]);
    const [continent, setContinent] = useState([]);
    const [regions, setRegions] = useState([]);
    const [region, setRegion] = useState([]);
    const [countries, setCountries] = useState([]);
    const [country, setCountry] = useState([]);
    const [languages, setLanguages] = useState([]);
    const [cities, setCities] = useState([]);
    const [sep1, setSep1] = useState("");
    const [sep2, setSep2] = useState("");

    
    useEffect(() => {
        fetch('/continents').then(res => res.json()).then(data => {
            setContinents(data.continents);
        });
    }, []);

    function onContinentClick(cont) {
        setContinent(cont);

        fetch('/regions/' + cont).then(res => res.json())
            .then(data => {
                setRegions(data.regions);
            })
        setSep1(">");
    }

    function onRegionClick(reg) {
        setRegion(reg);

        const GET_COUNTRIES = `
query getCountries {
countries(filters: {continent: "${continent}", region: "${reg}"}) {
edges {
node {
code 
name 
continent 
region 
surfacearea 
indepyear 
population 
lifeexpectancy 
gnp 
gnpold 
localname 
governmentform 
headofstate 
capital 
code2
}
}
}
}
`;

        fetch('/graphql?query=' + GET_COUNTRIES).then(res => res.json())
            .then(data => {
                setCountries(data.data.countries.edges);
            });

        setSep2(">");
    }

    function onCountryClick(country) {
        setCountry(country);

        const GET_LANGUAGES = `
query getLangauages { 
countryLanguages(filters: {countrycode: "${country.code}"}) { 
edges{ 
node{ 
countrycode 
language 
isofficial 
percentage
}
}
}
}
`;

        const GET_CITIES = `
query getCities {
cities(filters: {countrycode: "${country.code}"}) {
edges {
node {
id 
name 
countrycode 
district 
population 
}
}
}
}

`;

        fetch('/graphql?query=' + GET_LANGUAGES).then(res => res.json())
            .then(data => {
                setLanguages(data.country_languages.edges);
            });

        fetch('/graphql?query=' + GET_CITIES).then(res => res.json())
            .then(data => {
                setCities(data.cities.edges);
            });
    }

    
    return (
        <div className="App App-header">
            <h1>World Database</h1>
            <div className="App-filters">
                <Continents continents={continents} onClick={onContinentClick} />
                <Regions regions={regions} onClick={onRegionClick} />
                <Countries countries={countries} onClick={onCountryClick} />
            </div>
            <p>{continent} {sep1} {region} {sep2} {country.name}</p>
            <div className="App-data">
                <CountryAttr country={country} />
                
            </div>
        </div>
    );
}

export default App;
