import React, { useState, useEffect } from 'react';
import { ButtonGroup, Button } from 'react-bootstrap';


export const Continents = ({ continents }) => {

    function getRegions(continent) {
        const [regions, setRegions] = useState([]);
        const display = "hidden";

        fetch('/regions/' + continent).then(res => res.json()).then(data => {
            setRegions(data.regions);
            display = "block";
        });
    }

    return (
        <div>
            <ButtonGroup key="continents" size="lg" className="mb-2">
                {continents.map(cont => <Button key={cont} variant="secondary" onClick={getRegions(cont)}>{cont}</Button>)}
            </ButtonGroup>
        </div>
    )
}
