import React, { useState, useEffect, ReactDOM } from 'react';
import './App.css';
import { Continents } from './components/Continents';
import { Regions } from './components/Regions';
import { Countries } from './components/Countries';
import { Accordion, Card, Button } from 'react-bootstrap';
import { useAccordionToggle } from 'react-bootstrap/AccordionToggle';
import 'bootstrap/dist/css/bootstrap.css';

function App() {
    const [continents, setContinents, ] = useState([]);
    const [continent, setContinent] = useState([]);
    const [regions, setRegions] = useState([]);
    const [region, setRegion] = useState([]);
    const [countries, setCountries] = useState([]);
    const [country, setCountry] = useState([]);
    const [cities, setCities] = useState([]);
    const [city, setCity] = useState([]);
    const [activeEventKey, setActiveEventKey] = useState("");

    const GET_COUNTRIES = `
query getCountries {
countries(filters: {continent: ${continent}, region: ${region}}) {
edges {
node {
code 
name 
continent 
region 
surfacearea 
indepyear 
population 
lifeexpectancy 
gnp 
gnpold 
localname 
governmentform 
headofstate 
capital 
code2
}
}
}
}
`;

    const GET_CITIES = `
query getCities {
cities(filters: {countrycode: "USA"}) {
edges {
node {
id 
name 
countrycode 
district 
population 
}
}
}
}

`;
    useEffect(() => {
        fetch('/continents').then(res => res.json()).then(data => {
            setContinents(data.continents);
        });

        //fetch('/graphql?query=' + GET_COUNTRIES).then(res => res.json())
        //    .then(data => {
        //        for (const cont of continents) {
        //            fetch('/regions/' + continent).then(res => res.json()).then(data => {
        //                cont_reg.cont = data.regions;
        //                console.log(cont);
        //            });
        //        }
        //        setCountries(data.countries);
        //    });

        
        //fetch('/graphql?query=' + GET_CITIES).then(res => res.json())
        //    .then(data => {
        //        setCities(data.cities);
        //    });
    }, []);

    function CustomToggle({ children, eventKey }) {
        console.log(children);
        const accordionOnClick = useAccordionToggle(eventKey, () => setActiveEventKey(eventKey)
        );

        return (
            <button
                className="App-accordion-toggle"
                type="button"
                onClick={accordionOnClick}>
                {children}
            </button>
        );
    }

    function onContinentClick(cont) {
        console.log(activeEventKey);
        setContinent(cont);

        fetch('/regions/' + cont).then(res => res.json())
            .then(data => {
                setRegions(data.regions);
            })
        
        setActiveEventKey("1");
    }

    
    return (
        <div className="App App-header">
            <h1 className="App-title">World Database</h1>
            <Accordion activeKey={activeEventKey}>
                <Card>
                    <Card.Header>
                    <CustomToggle eventKey="0">
                            Continent
                    </CustomToggle>
                        </Card.Header>
                    <Accordion.Collapse eventKey="0">
                        <Card.Body>
                            <Continents continents={continents} onClick={onContinentClick} />
                        </Card.Body>
                    </Accordion.Collapse>
                </Card>
                <Card>
                    <Accordion.Toggle className="App-accordion-toggle" as={Card.Header} eventKey="1">
                        Regions
                    </Accordion.Toggle>
                    <Accordion.Collapse eventKey="1" id="1">
                        <Card.Body>
                            <Regions regions={regions} continent={continent} />
                        </Card.Body>
                    </Accordion.Collapse>
                </Card>
            </Accordion>
        </div>
    );
}

export default App;
