import React from 'react';
import { ButtonGroup, Button } from 'react-bootstrap';


export const Regions = ({ regions, continent }) => {
    if (regions.length == 0) {
        return <div>Please select continent to see the regions.</div>
    }

    return (
        <div>
            <p className="App-accordion-toggle">{continent}</p>
            <ButtonGroup size="lg">
                {regions.map(reg =>
                    <Button variant="secondary" key={reg}>
                        {reg}
                    </Button>
                )}
            </ButtonGroup>
        </div>
    )
}
