import React, { useState, useEffect } from 'react';
import { From, Button, Form } from 'react-bootstrap';

export const AddCityForm = ({ show, country }) => {
    const handleSubmit = (event) => {
        const form = event.currentTarget;
        console.log(form);
        if (form.checkValidity() === false) {
            event.preventDefault();
            event.stopPropagation();
        }

        setValidated(true);

        const GET_CITIES = `
mutation addCity {
createCity(name: "") {
edges {
node {
id 
name 
countrycode 
district 
population 
}
}
}
}

`;

        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify()
        }
    }

    return (
        <Form inline noValidate validated={validated} onSubmit={handleSubmit}>
            <Form.Row>
                <Form.Group as={Col} md="4" controlId="name">
                    <Form.Control type="text" placeholder="City Name" required />
                </Form.Group>
                <Form.Group as={Col} md="4" controlId="district">
                    <Form.Control type="text" placeholder="District" required />
                </Form.Group>
                <Form.Group as={Col} md="4" controlId="population">
                    <Form.Control type="number" placeholder="Population" required />
                </Form.Group>
                <Button as={Col} variant="success" type="submit">
                    Submit
            </Button>
            </Form.Row>
        </Form>
    )
}
