import React from 'react';
import BootstrapTable from 'react-bootstrap-table-next';

export const Language = ({ languages }) => {
    if (!languages || languages.length == 0) {
        return <div></div>
    }

    let languages_ls = [];

    for (const language of languages) {
        languages_ls.push([language.language, language.isofficial, language.percentage]);
    }

    const columns = [{
        dataField: 'name',
        text: 'Language Name',
        sort: true
    }, {
        dataField: 'isofficial',
        text: 'Official',
        sort: true
    }, {
        dataField: 'percentage',
        text: 'Percentage',
        sort: true
    }];

    return (
        <div>
            <p>Languages In Use</p>
            <BootstrapTable keyField="name" data={languages_ls} columns={columns} />
            </div>
    )
}
