import React, { useState, useEffect } from 'react';
import { From, Button, Form, Col } from 'react-bootstrap';

export const AddCityForm = ({ show, country }) => {
    const [validated, setValidated] = useState(false);

    const handleSubmit = (event) => {
        const form = event.currentTarget;
        event.preventDefault();

        if (form.checkValidity() === false) {
            event.stopPropagation();
        }

        setValidated(true);

        console.log(event.target.formName.value);

//        const ADD_CITY = `
//mutation addCity {
//createCity(name: "${this.name.value}", countrycode: "${country}", ditrict: "${this.district.value}", population: ${this.population.value}) {
//city {
//id 
//name 
//countrycode 
//district 
//population
//}
//}
//}

//`;

//        const requestOptions = {
//            method: 'POST',
//            headers: { 'Content-Type': 'application/json' },
//            body: JSON.stringify(ADD_CITY)
//        };

        //fetch('/graphql', requestOptions)
        //    .then(response => response.json())
        //    .then(data => {
        //        console.log(data);
        //    });
    };

    if (show) {
        return (
            <Form inline noValidate validated={validated} onSubmit={handleSubmit} style={{ width: "100%" }}>
                <Form.Row className="align-items-center">
                    <Col xs="auto">
                        <Form.Group controlId="formName">
                            <Form.Control inputRef={(ref) => { this.name = ref }} className="mb-4" type="text" placeholder="City Name" required />
                            </Form.Group>
                    </Col>
                    <Col xs="auto">
                        <Form.Group controlId="formDistrict">
                            <Form.Control inputRef={(ref) => { this.district = ref }} className="mb-4" type="text" placeholder="District" required />
                            </Form.Group>
                    </Col>
                        <Col xs="auto">
                            <Form.Group controlId="formPopulation">
                            <Form.Control inputRef={(ref) => { this.popluation = ref }} className="mb-4" type="number" placeholder="Population" required />
                            </Form.Group>
                    </Col>
                    <Col xs="auto">
                        <Button className="mb-4" variant="success" type="submit">
                            Submit
                    </Button>
                    </Col>
                </Form.Row>
            </Form>
        )
    }

    return <div></div>
}
