import React, { useState, useEffect, ReactDOM } from 'react';
import './App.css';
import { Continents } from './components/Continents';
import { Regions } from './components/Regions';
import { Countries } from './components/Countries';
import { TreeView } from 'react-bootstrap-treeview';
import 'bootstrap/dist/css/bootstrap.css';

function App() {
    const [continents, setContinents, ] = useState([]);
    const [continent, setContinent] = useState([]);
    const [regions, setRegions] = useState([]);
    const [region, setRegion] = useState([]);
    const [countries, setCountries] = useState([]);
    const [country, setCountry] = useState([]);
    const [cities, setCities] = useState([]);
    const [city, setCity] = useState([]);
    const [activeEventKey, setActiveEventKey] = useState("");

    

    const GET_CITIES = `
query getCities {
cities(filters: {countrycode: "USA"}) {
edges {
node {
id 
name 
countrycode 
district 
population 
}
}
}
}

`;
    useEffect(() => {
        fetch('/continents').then(res => res.json()).then(data => {
            setContinents(data.continents);
        });

       

        
        //fetch('/graphql?query=' + GET_CITIES).then(res => res.json())
        //    .then(data => {
        //        setCities(data.cities);
        //    });
    }, []);

    function CustomToggle({ children, eventKey }) {
        console.log(children);
        const accordionOnClick = useAccordionToggle(eventKey, () => setActiveEventKey(eventKey)
        );

        return (
            <button
                className="App-accordion-toggle"
                type="button"
                onClick={accordionOnClick}>
                {children}
            </button>
        );
    }

    function onContinentClick(cont) {
        setContinent(cont);

        fetch('/regions/' + cont).then(res => res.json())
            .then(data => {
                setRegions(data.regions);
            })
        
        setActiveEventKey("1");
    }

    function onRegionClick(reg) {
        setRegion(reg);

        const GET_COUNTRIES = `
query getCountries {
countries(filters: {continent: ${continent}, region: ${reg}}) {
edges {
node {
code 
name 
continent 
region 
surfacearea 
indepyear 
population 
lifeexpectancy 
gnp 
gnpold 
localname 
governmentform 
headofstate 
capital 
code2
}
}
}
}
`;

        fetch('/graphql?query=' + GET_COUNTRIES).then(res => res.json())
            .then(data => {
                setCountries(data.countries);
            });
    }

    
    return (
        <div className="App App-header">
            <h1 className="App-title">World Database</h1>
            <Continents continents={continents} onClick={onContinentClick} />
        </div>
    );
}

export default App;
