import React, { useState } from 'react';
import { Dropdown } from 'react-bootstrap';


export const Regions = ({ continent, onClick }) => {
    const [regions, setRegions] = useState([]);

    if (continent && continent.length > 0 && regions.length === 0) {
        fetch('/regions/' + continent).then(res => res.json())
            .then(data => {
                setRegions(data.regions);
            });
    } else if ((!continent || continent.length === 0) && regions.length !== 0) {
        setRegions([]);
    }

    return (
        <Dropdown className="App-filter">
            <Dropdown.Toggle variant="success" id="region-dropdown">
                Region
                </Dropdown.Toggle>
            <Dropdown.Menu>
                {regions.map(reg =>
                    <Dropdown.Item key={reg} onClick={() => onClick(reg)}>
                        {reg}
                    </Dropdown.Item>
                )}
            </Dropdown.Menu>
        </Dropdown>
    )
}
