import React from 'react';
import { ButtonGroup, Button } from 'react-bootstrap';


export const Regions = ({ regions, continent }) => {
    console.log(regions.length)
    if (regions.length != 0) {
        return (
            <div>
                <p className="App-accordion-toggle">{continent}</p>
                <ButtonGroup size="lg">
                    {regions.map(reg =>
                        <Button variant="secondary" key={reg}>
                            {reg}
                        </Button>
                    )}
                </ButtonGroup>
            </div>
        )
    }

    return (
        <div>
            <p>Please select continent to see the regions.</p>
        </div>
    )
}
