import React from 'react';
import { ButtonGroup, Button } from 'react-bootstrap';


export const Regions = ({ regions }) => {
    let content = "Please select continent to see the regions.";

    if (regions.length != 0) {
        content = (
            <ButtonGroup size="lg">
                {regions.map(reg =>
                    <Button variant="secondary" key={reg}>
                        {reg}
                    </Button>
                )}
            </ButtonGroup>
        );
    }
    return (
        <div>{content}</div>
    )
}
