import React, { useState, useEffect } from 'react';
import './App.css';
import { Continents } from './components/Continents';
import { Regions } from './components/Regions';
import { Countries } from './components/Countries';
import 'bootstrap/dist/css/bootstrap.css';

function App() {
    const [continents, setContinents] = useState([]);
    const [regions, setRegions] = useState([]);
    const [countries, setCountries] = useState([]);
    const [cities, setCities] = useState([]);
    const GET_COUNTRIES = `
query getCountries {
countries(filters: {continent: "North America", region: "North America"}) {
edges {
node {
code 
name 
continent 
region 
surfacearea 
indepyear 
population 
lifeexpectancy 
gnp 
gnpold 
localname 
governmentform 
headofstate 
capital 
code2
}
}
}
}
`;

    const GET_CITIES = `
query getCities {
cities(filters: {countrycode: "USA"}) {
edges {
node {
id 
name 
countrycode 
district 
population 
}
}
}
}

`;

    useEffect(() => {
        fetch('/continents').then(res => res.json()).then(data => {
            setContinents(data.continents);
        });

        fetch('/regions/Asia').then(res => res.json()).then(data => {
            setRegions(data.regions);
        });

        fetch('/graphql?query=' + GET_COUNTRIES).then(res => res.json())
            .then(data => {
                setCountries(data.countries);
        });

        fetch('/graphql?query=' + GET_CITIES).then(res => res.json())
            .then(data => {
                setCities(data.cities);
            });
    }, []);

    return (
        <div className="App">
            <Continents continents={continents} />
            <Regions regions={regions} />
            <Countries countries={countries} />
        </div>
    );
}

export default App;
