import React, { useState, useEffect, useRef } from 'react';
import { From, Button, Form, Col } from 'react-bootstrap';

export const AddCityForm = ({ show, country, mutated }) => {
    const [validated, setValidated] = useState(false);
    const [responseMessage, setResponseMessage] = useState("");
    const formRef = useRef(null);

    const handleSubmit = (event) => {
        const form = event.currentTarget;
        event.preventDefault();

        if (form.checkValidity() === false) {
            event.stopPropagation();
        }

        setValidated(true);

        const ADD_CITY = `
mutation addCity {
createCity(name: "${event.target.formName.value}", countrycode: "${country.code}", district: "${event.target.formDistrict.value}", population: ${event.target.formPopulation.value}) {
city {
id 
name 
countrycode 
district 
population
}
}
}

`;

        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ query: ADD_CITY })
        };

        fetch('/graphql', requestOptions)
            .then(response => response.json())
            .then(data => {
                console.log(data);
                if (data.data) {
                    setResponseMessage(data.data.createCity.city.name + " is successfully added.");
                    mutated = true;
                } else {
                    setResponseMessage("System failed to add city.");
                }
            });
        console.log(formRef);
        formRef.current.reset();
        setValidated(false);
    };

    if (show) {
        return (
            <div>
                <Form inline noValidate validated={validated} onSubmit={handleSubmit} style={{ width: "100%" }} ref={ formRef }>
                <Form.Row className="align-items-center">
                    <Col xs="auto">
                        <Form.Group controlId="formName">
                            <Form.Control className="mb-4" type="text" placeholder="City Name" required />
                            </Form.Group>
                    </Col>
                    <Col xs="auto">
                        <Form.Group controlId="formDistrict">
                            <Form.Control className="mb-4" type="text" placeholder="District" required />
                            </Form.Group>
                    </Col>
                        <Col xs="auto">
                            <Form.Group controlId="formPopulation">
                            <Form.Control className="mb-4" type="number" placeholder="Population" required />
                            </Form.Group>
                    </Col>
                    <Col xs="auto">
                        <Button className="mb-4" variant="success" type="submit">
                            Submit
                    </Button>
                    </Col>
                </Form.Row>
                </Form>
                <p>{responseMessage}</p>
                </div>
        )
    }

    setResponseMessage("");
    return <div></div>
}
